const App = () => {
  const course = 'Half Stack application development'
  const part1 = 'Fundamentals of React'
  const exercises1 = 10
  const part2 = 'Using props to pass data'
  const exercises2 = 7
  const part3 = 'State of a component'
  const exercises3 = 14

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total total={exercises1 + exercises2 + exercises3}/>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part content={part1} excercise={exercises1} />
      <Part content={part2} excercise={exercises2} />
      <Part content={part3} excercise={exercises3} />
    </div>
  )
}
const Header =(props) =>{
  return(

    <h1>{props.course}</h1>

  )
}


const Part = (props) =>{
  return( <p>{props.content} {props.excercise}</p>)
}
const Total = (props) =>{
  return(
    <p>
      {props.total}
    </p>
  )
}
export default App
