const App = () => {
  const course = 'Half Stack application development'
  const part1 = {
    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }
 const arrayOfParts = [part1, part2, part3]
  return (
    <div>
      <Header course={course} />
      <Content part = {arrayOfParts}/>
      <Total parts={arrayOfParts}/>

    </div>
  )
}

const Content = (props) => {
  console.log(props)
  return (
    <div>
      <Part part = {props.part[0]["name"]} exercises ={props.part[0]["exercises"]} />
      <Part part = {props.part[1]["name"]} exercises ={props.part[1]["exercises"]} />
      <Part part = {props.part[2]["name"]} exercises ={props.part[2]["exercises"]} />



    </div>
  )
}
const Header =(props) =>{
    console.log(props)

  return(

    <h1>{props.course}</h1>

  )
}


const Part = (props) =>{

  console.log(props)

  return( <p>{props.part}</p>)
}

const Total = (props) =>{
  console.log(props)
  const array = props.parts
  const i = 0
  array.forEach(element => {
    i +=  element["excercises"]
  });
  return(
    <p>
      {i}
    </p>
  )
}
export default App
