    name: 'Fundamentals of React',
    exercises: 10
  }
  const part2 = {
    name: 'Using props to pass data',
    exercises: 7
  }
  const part3 = {
    name: 'State of a component',
    exercises: 14
  }

  return (
    <div>
      <Header course={course} />
      <Content />
      <Total total={exercises1 + exercises2 + exercises3}/>
    </div>
  )
}

const Content = (props) => {
  return (
    <div>
      <Part content={props.part} excercise={props.exercises} />

    </div>
  )
}
const Header =(props) =>{
  return(

    <h1>{props.course}</h1>

  )
}


const Part = (props) =>{
  return( <p>{props.content} {props.excercise}</p>)
}
const Total = (props) =>{
  return(
    <p>
      {props.total}
    </p>
  )
}
export default App
