import { useState } from "react";

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);
  const array = [good, neutral, bad];
  const setGoodValue = (newValue) => {
    newValue = good + 1;
    setGood(newValue);
  };

  const setNeutralValue = (newValue) => {
    newValue = good + 1;
    setNeutral(newValue);
  };
  const setBadValue = (newValue) => {
    newValue = good + 1;
    setBad(newValue);
  };
  return (
    <div>
      <Header />
      <Button name="Good" handleClick={setGoodValue} />
      <Button name="Neutral" handleClick={setNeutralValue} />
      <Button name="Bad" handleClick={setBadValue} />
      <Statistics stats={array}></Statistics>
    </div>
  );
};

const Header = () => {
  return <h1>give feedback</h1>;
};

const Button = (props) => (
  <button onClick={props.handleClick}>{props.name}</button>
);

const Statistics = (props) => {
  const array = props.stats;
  const good = array[0];
  const neutral = array[1];
  const bad = array[2];
  return (
    <>
      <h1>statistics</h1>
      <p></p>
      <p>good {good}</p>
      <p>neutral {neutral}</p>
      <p>bad {bad}</p>
    </>
  );
};
export default App;
