import { useState } from "react";

const App = () => {
  // save clicks of each button to its own state
  const [good, setGood] = useState(0);
  const [neutral, setNeutral] = useState(0);
  const [bad, setBad] = useState(0);
  const valueArray = [good, neutral, bad];

  const setGoodValue = (newValue) => {
    newValue = good + 1;
    setGood(newValue);
  };

  const setNeutralValue = (newValue) => {
    newValue = neutral + 1;
    setNeutral(newValue);
  };
  const setBadValue = (newValue) => {
    newValue = bad + 1;
    setBad(newValue);
  };

  return (
    <div>
      <Header />
      <Button name="Good" handleClick={setGoodValue} />
      <Button name="Neutral" handleClick={setNeutralValue} />
      <Button name="Bad" handleClick={setBadValue} />
      <Statistics values={valueArray} />
    </div>
  );
};
const Feedback = ({ name, value }) => {
  return (
    <p>
      {name} {value}
    </p>
  );
};

const Header = () => {
  return <h1>give feedback</h1>;
};

const Button = (props) => (
  <button onClick={props.handleClick}>{props.name}</button>
);

const Statistics = (props) => {
  const array = props.values;
  const good = array[0];
  const bad = array[2];
  const neutral = array[1];
  var all = 0;

  array.forEach((element) => {
    all += element;
  });
  const average = (good - bad) / all;

  const positive = (good / all) * 100;
  return (
    <>
      <h1>statistics</h1>
      <Feedback name="Good" value={good} />
      <Feedback name="Neutral" value={neutral} />
      <Feedback name="Bad" value={bad} />
      <p>All {all}</p>
      <p>Average {average}</p>
      <p>Positive {positive}%</p>
    </>
  );
};
export default App;
